recordList.event:
if sheet==nil then return end;
if "$(rclName)" == "campoDasPericias" then if sheet.$(rclName) == nil then sheet.$(rclName) = {} end
local nodes = NDB.getChildNodes(sheet.$(rclName)); if #nodes > 0 then return end;
local skills = {"Artes", "Atletismo", "Suborno", "Charme", "Charme para Animais", "Escalar", "Calmo", "Resistencia ao Alcool", "Esquiva", "Dirigir", "Tenacidade", "Entretenimento", "Apostar", "Fofoca", "Pechinchar", "Intimidação", "Intuição", "Liderança", "Corpo a corpo (Basico)", "Corpo a corpo", "Navegação", "Sobrevivencia na Natureza", "Percepção", "Cavalgar", "Remar", "Furtividade"} local keys = {"dex", "ag", "fel", "fel", "wp", "s", "wp", "t", "ag", "ag", "t", "fel", "int", "fel", "fel", "s", "i", "fel", "ws", "ws", "i", "int", "i", "ag", "s", "ag"}
for i=1, #skills, 1 do local node = self.$(rclName):append(); if node~= nil then node.wsTotal = sheet.wsTotal node.bsTotal = sheet.bsTotal node.sTotal = sheet.sTotal node.tTotal = sheet.tTotal node.iTotal = sheet.iTotal node.agTotal = sheet.agTotal node.dexTotal = sheet.dexTotal node.intTotal = sheet.intTotal node.wpTotal = sheet.wpTotal node.felTotal = sheet.felTotal node.nome = skills[i] node.key = keys[i] end end
self.$(rclName):sort(); end
